package com.flash.blogbackup.config;import java.util.HashMap;import java.util.Map;/** * @author cairne huangyihua@diandian.com * @date Sep 27, 2011 */public class Config {    public static final Config defaultConfig = new Config();    static {        defaultConfig.put("ProjectName", "Flash Blog Backup");    }    public Config() {        configMap = new HashMap<String, String>();    }    private Map<String, String> configMap;    /**     * @return the configMap     */    public Map<String, String> getConfigMap() {        return configMap;    }    /**     * @param configMap the configMap to set     */    public void setConfigMap(Map<String, String> configMap) {        this.configMap = configMap;    }    public void put(String name, String object) {        configMap.put(name, object);    }    public int getInt(String name) {        return Integer.parseInt(configMap.get(name));    }    public double getDouble(String name) {        return Double.parseDouble(configMap.get(name));    }    public String getString(String name) {        return (String) configMap.get(name);    }}