package com.flash.blogbackup.console;import javax.swing.JProgressBar;/** * @author cairne huangyihua@diandian.com * @date Oct 4, 2011 */public class ProgressBarObserver implements JComponentObserver<JProgressBar> {    public static ProgressBarObserver single = new ProgressBarObserver();    private ProgressBarObserver() {    }    private JProgressBar progressBar;    private int progress = 0;    /**     * @todo 刷新UI组件     */    public void refresh() {    }    public void incProgress(int delta) {        if (progressBar == null) {            throw new UnsupportedOperationException();        }        progress += delta;        progressBar.setValue(progress);        refresh();    }    public void setComponent(JProgressBar progressBar) {        this.progressBar = progressBar;    }}