package com.flash.blogbackup.model;import java.io.Serializable;import java.util.LinkedList;import java.util.List;/** * @author cairne huangyihua@diandian.com * @date Sep 27, 2011 */public class Blog implements Serializable {    private static final long serialVersionUID = -3746707633760101143L;    private List<BlogItem> items = null;    private String name;    private String url;    private BlogType type;    private String userName;    private String password;    public String getName() {        return name;    }    public String getUrl() {        return url;    }    public Blog(BlogType type) {        this.type = type;    }    public Blog(BlogType type, String url) {        this.url = url;        this.type = type;    }    public List<BlogItem> getItems() {        if (items == null) {            items = new LinkedList<BlogItem>();        }        return items;    }    public void addItem(BlogItem item) {        getItems().add(item);    }    public BlogItem getItem(int index) {        if (index > getItems().size()) {            return getItems().get(getItems().size() - 1);        }        return getItems().get(index);    }    /**     * @param userName the userName to set     */    public void setUserName(String userName) {        this.userName = userName;    }    /**     * @param password the password to set     */    public void setPassword(String password) {        this.password = password;    }    /**     * @return the userName     */    public String getUserName() {        return userName;    }    /**     * @return the password     */    public String getPassword() {        return password;    }    /**     * @return the type     */    public BlogType getType() {        return type;    }    public String toString() {        return type + " " + url;    }}