package com.flash.blogbackup.model;import java.io.Serializable;import java.util.Date;import java.util.List;import com.flash.blogbackup.reader.BlogReader;/** * @author cairne huangyihua@diandian.com * @date Sep 27, 2011 */public class BlogItem implements Serializable {    private static final long serialVersionUID = -46763491728091955L;    private String url;    private String title;    private String category;    private String tags;    private String content;    private Date date;    private List<String> comments;    public BlogItem(String title, String url) {        this.title = title;        this.url = url;    }    public BlogItem(String title, String category, String tags, String content, Date date,            List<String> comments) {        this.title = title;        this.category = category;        this.tags = tags;        this.content = content;        this.date = date;        this.comments = comments;    }    public void fetch(BlogReader blogReader) {        blogReader.fetchBlogItem(this);    }    public String getUrl() {        return url;    }    public String getTitle() {        return title;    }    public String getContent() {        return content;    }    public Date getDate() {        return date;    }    public String getCategory() {        return category;    }    public String getTags() {        return tags;    }    public List<String> getComments() {        return comments;    }    public String toString() {        return title + " " + url;    }}